import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import './App.css';

// 上传页面组件
function UploadPage() {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [outline, setOutline] = useState(null);
  const [pptContent, setPptContent] = useState(null);
  const navigate = useNavigate();

  const handleFileUpload = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.size > 10 * 1024 * 1024) {
      setMessage('文件大小不能超过 10MB');
      return;
    }
    setFile(selectedFile);
    setMessage('');
  };

  const handleSubmit = async () => {
    if (!file) {
      setMessage('请先选择文件');
      return;
    }

    setIsLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setMessage(data.message);
      
      if (data.data) {
        setOutline(data.data.outline);
        setPptContent(data.data.pptContent);
      }
    } catch (error) {
      console.error('上传错误:', error);
      setMessage('上传失败：' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const renderOutline = () => {
    if (!outline) return null;
    
    try {
      // 清理 JSON 字符串中的 ```json 和 ``` 标记
      const cleanedOutline = outline.replace(/```json\n/, '').replace(/\n```$/, '');
      const outlineData = typeof cleanedOutline === 'string' ? JSON.parse(cleanedOutline) : cleanedOutline;
      
      if (outlineData.slides && Array.isArray(outlineData.slides)) {
        return (
          <div className="outline-container">
            <h2>演讲大纲</h2>
            {outlineData.slides.map((slide) => (
              <div key={slide.page_number} className="outline-slide">
                <h3>第 {slide.page_number} 页</h3>
                <h4>{slide.subtitle}</h4>
                <div className="slide-content">
                  <div className="slide-notes">
                    <strong>备注：</strong> {slide.notes}
                  </div>
                  <div className="slide-points">
                    <strong>要点：</strong>
                    <ul>
                      {slide.content.map((point, index) => (
                        <li key={index}>{point}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              </div>
            ))}
            <button 
              onClick={handleGenerateScript}
              className="generate-script-btn"
              disabled={isLoading}
            >
              {isLoading ? '正在生成脚本...' : '开始生成脚本'}
            </button>
          </div>
        );
      } else {
        console.error('大纲数据格式不正确:', outlineData);
        return <div>大纲数据格式不正确</div>;
      }
    } catch (error) {
      console.error('解析大纲错误:', error);
      return <div>大纲解析错误: {error.message}</div>;
    }
  };

  const handleGenerateScript = async () => {
    try {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/api/generate-script', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          pptContent: pptContent,
          outline: outline
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      navigate('/script-editor', { 
        state: { 
          script: data.script,
          pptContent: pptContent,
          outline: outline
        } 
      });
    } catch (error) {
      console.error('生成脚本错误:', error);
      setMessage('生成脚本失败：' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // 渲染 PPT 内容的函数
  const renderPPTContent = () => {
    if (!pptContent || !pptContent.slides) return null;
    
    return (
      <div className="ppt-content">
        <h2>PPT 内容</h2>
        <div className="slides-container">
          {pptContent.slides.map((slide, index) => (
            <div key={index} className="slide">
              <h3>幻灯片 {index + 1}</h3>
              <p><strong>标题：</strong> {slide.title}</p>
              <p><strong>内容：</strong> {slide.content}</p>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="upload-container">
      <h1>AI视频生成器</h1>
      <div className="upload-form">
        <input 
          type="file" 
          accept=".pptx" 
          onChange={handleFileUpload}
          disabled={isLoading}
        />
        <button 
          onClick={handleSubmit} 
          disabled={isLoading || !file}
        >
          {isLoading ? '正在生成大纲...' : '上传并生成大纲'}
        </button>
      </div>
      
      {message && (
        <p className={`message ${message.includes('失败') ? 'error' : 'success'}`}>
          {message}
        </p>
      )}

      <div className="content-container">
        {renderPPTContent()}

        {renderOutline()}
      </div>
    </div>
  );
}

// 添加脚本编辑器组件
function ScriptEditor() {
  const location = useLocation();
  const navigate = useNavigate();
  const [scripts, setScripts] = useState([]);
  const [regenerateRequirements, setRegenerateRequirements] = useState({});
  const [isLoading, setIsLoading] = useState({});

  useEffect(() => {
    if (location.state?.script) {
      const scriptData = typeof location.state.script === 'string' 
        ? JSON.parse(location.state.script) 
        : location.state.script;
      setScripts(scriptData);
    }
  }, [location.state]);

  const handleRegenerateScript = async (pageIndex) => {
    try {
      setIsLoading(prev => ({ ...prev, [pageIndex]: true }));
      
      const response = await fetch('http://localhost:5000/api/regenerate-script', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          pageIndex,
          requirement: regenerateRequirements[pageIndex],
          pptContent: location.state.pptContent,
          outline: location.state.outline
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setScripts(prev => {
        const newScripts = [...prev];
        newScripts[pageIndex] = data.script;
        return newScripts;
      });
    } catch (error) {
      console.error('重新生成脚本错误:', error);
    } finally {
      setIsLoading(prev => ({ ...prev, [pageIndex]: false }));
    }
  };

  return (
    <div className="script-editor">
      <h1>演讲脚本</h1>
      <div className="script-content">
        {scripts.map((script, index) => (
          <div key={index} className="script-page">
            <h2>第 {index + 1} 页</h2>
            <div className="page-content">
              {script.content}
            </div>
            <div className="regenerate-section">
              <textarea
                placeholder="输入特殊要求..."
                value={regenerateRequirements[index] || ''}
                onChange={(e) => setRegenerateRequirements(prev => ({
                  ...prev,
                  [index]: e.target.value
                }))}
              />
              <button 
                onClick={() => handleRegenerateScript(index)}
                disabled={isLoading[index]}
              >
                {isLoading[index] ? '重新生成中...' : '重新生成'}
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

// 主应用组件
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<UploadPage />} />
        <Route path="/script-editor" element={<ScriptEditor />} />
      </Routes>
    </Router>
  );
}

export default App;
