import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import './App.css';

// 上传页面组件
function UploadPage() {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [outline, setOutline] = useState(null);
  const [pptContent, setPptContent] = useState(null);
  const navigate = useNavigate();

  const handleFileUpload = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.size > 10 * 1024 * 1024) {
      setMessage('文件大小不能超过 10MB');
      return;
    }
    setFile(selectedFile);
    setMessage('');
  };

  const handleSubmit = async () => {
    if (!file) {
      setMessage('请先选择文件');
      return;
    }

    setIsLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setMessage(data.message);
      
      if (data.data) {
        setOutline(data.data.outline);
        setPptContent(data.data.pptContent);
      }
    } catch (error) {
      console.error('上传错误:', error);
      setMessage('上传失败：' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleGenerateScript = async () => {
    try {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/api/files/generate-script', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          pptContent: pptContent,
          outline: outline
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log('生成的脚本:', data);
      
      // 导航到脚本编辑页面
      navigate('/script-editor', { 
        state: { 
          script: data.script,
          pptContent: pptContent
        } 
      });
    } catch (error) {
      console.error('生成脚本错误:', error);
      setMessage('生成脚本失败：' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // 渲染 PPT 内容的函数
  const renderPPTContent = () => {
    if (!pptContent || !pptContent.slides) return null;
    
    return (
      <div className="ppt-content">
        <h2>PPT 内容</h2>
        <div className="slides-container">
          {pptContent.slides.map((slide, index) => (
            <div key={index} className="slide">
              <h3>幻灯片 {index + 1}</h3>
              <p><strong>标题：</strong> {slide.title}</p>
              <p><strong>内容：</strong> {slide.content}</p>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="upload-container">
      <h1>AI视频生成器</h1>
      <div className="upload-form">
        <input 
          type="file" 
          accept=".pptx" 
          onChange={handleFileUpload}
          disabled={isLoading}
        />
        <button 
          onClick={handleSubmit} 
          disabled={isLoading || !file}
        >
          {isLoading ? '正在生成大纲...' : '上传并生成大纲'}
        </button>
      </div>
      
      {message && (
        <p className={`message ${message.includes('失败') ? 'error' : 'success'}`}>
          {message}
        </p>
      )}

      <div className="content-container">
        {renderPPTContent()}

        {outline && (
          <div className="outline-container">
            <h2>生成的大纲</h2>
            <pre className="outline-content">
              {typeof outline === 'string' ? outline : JSON.stringify(outline, null, 2)}
            </pre>
            <button 
              onClick={handleGenerateScript}
              className="generate-script-btn"
              disabled={isLoading}
            >
              {isLoading ? '正在生成脚本...' : '开始生成脚本'}
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

// 添加脚本编辑器组件
function ScriptEditor() {
  const location = useLocation();
  const script = location.state?.script;

  if (!script) {
    return <div>没有找到脚本数据</div>;
  }

  
  const scriptData = typeof script === 'string' ? JSON.parse(script) : script;

  return (
    <div className="script-editor">
      <h1>脚本预览</h1>
      <div className="script-content">
        {Array.isArray(scriptData) ? (
          scriptData.map((page, index) => (
            <div key={index} className="script-page">
              <h2>第 {index + 1} 页</h2>
              <div className="page-content">
                {page.content}
              </div>
            </div>
          ))
        ) : (
          <div className="script-page">
            <div className="page-content">
              {script}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// 主应用组件
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<UploadPage />} />
        <Route path="/script-editor" element={<ScriptEditor />} />
      </Routes>
    </Router>
  );
}

export default App;
